mapper.select:
SELECT	count(1)	as cnt
FROM	MSTvaDANCODE
WHERE	rflag	= #{rflag}
;

SELECT	DISTINCT dancode
FROM	MSTvaDANCODE
WHERE	rflag	= #{rflag}
;

mapper.update:
		UPDATE	MSTvaDANCODE SET rflag = '9'
	    WHERE	rflag	= '1'
  		;

delete.foreach:
DELETE
FROM 	MSTvaDANCODE
WHERE	dancode	= #{item.dancode}
;
DELETE
FROM 	MSTvaMEMBER
WHERE	dancode	= #{item.dancode}
;
